mapper.update:
UPDATE  public.mdata_REWARD
SET     MODIFIED_BY = #{META.MODIFIED_BY},
        MODIFIED_DATE = NOW()
        ,REWARD_NAME = #{REWARD_NAME}
        ,REWARD_DESC = #{REWARD_DESC}
        ---
        ,VALID_STARTDATE = #{VALID_STARTDATE}::timestamp
        ,VALID_ENDDATE = #{VALID_ENDDATE}::timestamp
        ---
        ,REWARD_OUTPUT = #{REWARD_OUTPUT}::public.mode_REWARD_OUTPUT
        ,LIFESPAN_MODE = #{LIFESPAN_MODE}::public.mode_REWARD_LIFESPAN
        ,DATE_UNBOX = #{LIFESPAN_MODE_INFO}
WHERE REWARD_CODE = #{REWARD_CODE}
